import React from "react";
import {
  StyleSheet,
  Text,
  View,
  ImageBackground,
  TextInput,
  Button,
} from "react-native";

export default function RegistrationScreen() {
    return (
    <View style={styles.container}>
      <ImageBackground
        style={styles.image}
        source={require("./../assets/images/photo-bg.jpg")}
            >
            <View style={styles.form}>
                <View>
                <Text style={styles.title}>Регистрация</Text>        
                    <TextInput style={styles.input} placeholder={"Логин"} />       
                </View>
                <View style={{ marginTop: 20 }}>      
                    <TextInput style={styles.input} placeholder={"Адрес электронной почты"} />       
                </View>    
            <View style={{ marginTop: 20 }}>
            <TextInput
              style={styles.input}
              textAlign={"center"}
              secureTextEntry={true}
              placeholder={"Пароль"}
            />
          </View>
          <Button style={{ marginTop: 20 }} title="Зарегистрироваться" />    
            </View>                 
      </ImageBackground>
    </View>
    );

}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  image: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center",
   },
    input: {
        borderWidth: 1,
        borderColor: "#E8E8E8",
        height: 50,
        borderRadius: 8,
        marginHorizontal: 16,
        backgroundColor: "#F6F6F6",
        textAlign: "center",
    },
    form: {
      marginHorizontal: 40,  
    },
    title: {
        color: "#212121",
        marginBottom: 10,
        fontSize: 30,
        fontWeight: 500,
        textAlign: "center",
        placeholderTextColor: "#BDBDBD",
    },
    button: {

    },
});
