import React from "react";
import { View,
         Text,
         StyleSheet,
         TouchableOpacity,
         Image, 
        } from "react-native";
import { MaterialIcons } from '@expo/vector-icons'; 

export default function PostsScreen() {
  return (
    <View style={styles.container}>
      <View style={styles.header}>
          <View style={styles.wrapper}>
            <Text style={styles.title}>Публикации   
            </Text>
          </View>
          <TouchableOpacity style={styles.logoutBtn}>        
            <MaterialIcons 
              name="logout" size={24}
              style={styles.logout}/> 
            </TouchableOpacity> 
      </View>
      <View style={styles.userContainer}>
        <Image
           style={styles.image}
           source={require("../../assets/images/photo-user-small.jpg")}
          >
         <Text style={styles.userName}></Text>   
        </Image>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    width: 375,
    maxHeight: 812,
  },
  logoutBtn:{
    marginLeft: "auto", 
    maxWidth: 24,
    backgroundColor: "transparent",
    borderColor: "none",
  },
  header: {
    flex: 1,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    padding: 11,
    maxHeight: 88,
    borderBottomWidth: 1,
    borderColor: "#c3c3c3",
  },
  wrapper:{
    marginLeft: "35%",
    maxWidth: 175,
  },
  logout:{
    color: "#c3c3c3",
  },
  title:{
    textAlign: "center",
    color: "#212121",
    fontWeight: 500,
    fontSize: 17,
    lineHeight: 22,
  },
  userContainer:{
    flex: 1,
    flexDirection: "row",
    alignItems: "center",
    marginTop: 32,
    marginLeft: 16,
    maxWidth: 171,
    maxHeight: 60,
    borderWidth: 1,
  },
  image:{
    width: 60,
    height: 60,
    resizeMode: "cover",
  }
});
